import { useState } from "react";
import LoginModal from "../components/LoginModal";
import "../styles/landing.css";

const Landing = () => {
  const [showLogin, setShowLogin] = useState(false);

  return (
    <div className="landing-container">

      {/* Header */}
      <header className="landing-header">
        <div className="header-left">
          <span className="gov-small">Government of India</span>
          <span className="gov-main">
            Unique Identification Authority of India
          </span>
        </div>

        <button
          className="login-btn"
          onClick={() => setShowLogin(true)}
        >
          Login
        </button>
      </header>

      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <h1>
            AUA / KUA Authentication
            <br />
            Onboarding Portal
          </h1>

          <p>
            Secure onboarding for Authentication User Agencies (AUA)
            and e-KYC User Agencies (KUA) under the Aadhaar Act, 2016.
          </p>

          <button
            className="primary-btn"
            onClick={() => setShowLogin(true)}
          >
            Login
          </button>
        </div>
      </section>

      {showLogin && <LoginModal closeModal={() => setShowLogin(false)} />}
    </div>
  );
};

export default Landing;
